function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }

   //Type appropriate comment here, and begin script below
	g_form.clearOptions('u_what_needed');
	if (newValue == "hr"){
		g_form.addOption('u_what_needed','Human Recources1','Human Recources1',1);
		g_form.addOption('u_what_needed','Human Recources2','Human Recources2',2);
		g_form.addOption('u_what_needed','Human Recources3','Human Recources3',3);
		console.log('executed');

	} else if (newValue == 'facilities'){
		g_form.addOption('u_what_needed','Facilities 1','Facilities 1',1);
		g_form.addOption('u_what_needed','Facilities 2','Facilities 2',2);
		g_form.addOption('u_what_needed','Facilities 3','Facilities 3',3);
		console.log('executed');
	} else if (newValue == 'legal'){
		g_form.addOption('u_what_needed','legal 1','legal 1',1);
		g_form.addOption('u_what_needed','legal 2','legal 2',2);
		g_form.addOption('u_what_needed','legal 3','legal 3',3);
		console.log('executed');
	} else if (isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed',whatneeded);
	}
}
