sys_script_include.script:
var GetCMDBDetails = Class.create();
// Extend the global.AbstractAjaxProcessor class
GetCMDBDetails.prototype = Object.extendsObject(global.AbstractAjaxProcessor,{
	// Define the getEmail function.  
	// Create a GlideRecord for the User table.
	// Use the sysparm_userID passed from the client side to retrieve a record from the User table.
	// Return the email address for the requested record
	getCI: function() {
		var CIRecord = new GlideRecord("cmdb_ci");
		CIRecord.get(this.getParameter('sysparm_ciID'));
		return CIRecord.asset_tag;
	},
	type: 'GetCMDBDetails'
});
